import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Cart() {
  const [cart, setCart] = useState(JSON.parse(localStorage.getItem("cart")||"[]"));
  const navigate = useNavigate();

  const checkout = () => {
    alert("Implement Stripe later");
    localStorage.removeItem("cart");
    setCart([]);
    navigate("/");
  };

  return (
    <div style={{maxWidth:900, margin:'20px auto'}}>
      <h2>Cart</h2>
      {cart.length===0 && <div>Your cart is empty</div>}
      {cart.map((c, i)=>(
        <div key={i} style={{display:'flex',gap:12,alignItems:'center',borderBottom:'1px solid #eee',padding:12}}>
          <img src={c.item.image || "/placeholder.png"} style={{width:80}} />
          <div style={{flex:1}}>
            <div>{c.item.name || c.item.title}</div>
            <div>Qty: {c.qty}</div>
          </div>
          <div>â‚¹{c.item.price || c.item.price}</div>
        </div>
      ))}
      {cart.length>0 && <button onClick={checkout}>Checkout</button>}
    </div>
  );
}
