import React, { useState } from 'react';
import API from '../api';
import { useNavigate } from 'react-router-dom';
export default function Auth(){
  const [isLogin, setIsLogin] = useState(true);
  const [form, setForm] = useState({ name:'', email:'', password:'' });
  const navigate = useNavigate();
  const submit = async (e) => {
    e.preventDefault();
    try {
      if(isLogin){
        const res = await API.post('/auth/login', { email: form.email, password: form.password });
        localStorage.setItem('token', res.data.token);
        localStorage.setItem('user', JSON.stringify(res.data.user));
      } else {
        const res = await API.post('/auth/register', { name: form.name, email: form.email, password: form.password });
        localStorage.setItem('token', res.data.token);
        localStorage.setItem('user', JSON.stringify(res.data.user));
      }
      navigate('/');
    } catch(err) { alert(err.response?.data?.message || err.message); }
  };
  return (
    <div style={{ maxWidth:420 }}>
      <h2>{isLogin ? 'Login' : 'Register'}</h2>
      <form onSubmit={submit}>
        {!isLogin && <div><label>Name</label><input value={form.name} onChange={e=>setForm({...form,name:e.target.value})} /></div>}
        <div><label>Email</label><input value={form.email} onChange={e=>setForm({...form,email:e.target.value})} /></div>
        <div><label>Password</label><input type="password" value={form.password} onChange={e=>setForm({...form,password:e.target.value})} /></div>
        <button type="submit">{isLogin ? 'Login' : 'Register'}</button>
      </form>
      <button onClick={()=>setIsLogin(!isLogin)}>{isLogin ? 'Switch to register' : 'Switch to login'}</button>
    </div>
  );
}
