import React, { useEffect, useState } from "react";
import { useLocation, Link } from "react-router-dom";

export default function VendorList() {
  const location = useLocation();
  const params = new URLSearchParams(location.search);

  // Get category from URL
  let category = params.get("category");

  // Map frontend categories → backend types
  const map = {
    restaurants: "restaurant",
    grocery: "grocery",
    fruits: "fruits"
  };

  const apiType = map[category] || ""; // "" = show all vendors

  const [vendors, setVendors] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function loadVendors() {
      try {
        const url = apiType
          ? `${process.env.REACT_APP_API_URL}/vendors?type=${apiType}`
          : `${process.env.REACT_APP_API_URL}/vendors`;

        const res = await fetch(url);
        const data = await res.json();
        setVendors(data);
      } catch (err) {
        console.log("Error loading vendors", err);
      } finally {
        setLoading(false);
      }
    }

    loadVendors();
  }, [apiType]);

  return (
    <div>
      <h1>{category ? category.toUpperCase() : "ALL VENDORS"}</h1>

      {loading && <p>Loading...</p>}

      {!loading && vendors.length === 0 && <p>No vendors found.</p>}

      <ul>
        {vendors.map((v) => (
          <li key={v._id}>
            <Link to={`/vendor/${v._id}`}>{v.name}</Link>
          </li>
        ))}
      </ul>
    </div>
  );
}
