import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const VendorList = () => {
  const { category } = useParams(); // restaurants, grocery, meats etc
  const [vendors, setVendors] = useState([]);
  const [loading, setLoading] = useState(true);

  // Map URL folder → backend category
  const categoryMap = {
    restaurants: "restaurant",
    grocery: "grocery",
    groceries: "grocery",
    fruits: "fruits",
    meat: "meat",
    meats: "meat",
  };

  const apiCategory = categoryMap[category];

  useEffect(() => {
    const fetchVendors = async () => {
      try {
        const res = await axios.get(
          `https://backendmodern.onrender.com/api/vendors`,
          {
            params: { category: apiCategory },
          }
        );

        setVendors(res.data);
        setLoading(false);
      } catch (err) {
        console.error("Error fetching vendors:", err);
        setLoading(false);
      }
    };

    fetchVendors();
  }, [apiCategory]);

  if (loading) return <p className="text-center mt-10">Loading...</p>;

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4 capitalize">
        {category} Vendors
      </h1>

      {vendors.length === 0 ? (
        <p>No vendors found.</p>
      ) : (
        <div className="grid grid-cols-1 gap-4">
          {vendors.map((vendor) => (
            <div
              key={vendor._id}
              className="p-4 border rounded shadow bg-white"
            >
              <h2 className="font-semibold text-lg">{vendor.name}</h2>
              <p className="text-sm text-gray-600">
                Type: {vendor.type}
              </p>
              <p className="text-sm text-gray-600">
                Address: {vendor.address}
              </p>
              <p className="text-sm">
                Status:{" "}
                {vendor.isApproved ? (
                  <span className="text-green-600">Approved</span>
                ) : (
                  <span className="text-red-600">Not Approved</span>
                )}
              </p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default VendorList;
