import React from "react";
import { Link, useNavigate } from "react-router-dom";

export default function Nav() {
  const navigate = useNavigate();
  const logged = !!localStorage.getItem("token");

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/auth");
  };

  return (
    <nav style={{display:'flex',alignItems:'center',gap:20,padding:16,background:'#fff',borderBottom:'1px solid #eee'}}>
      <Link to="/" style={{fontWeight:700,fontSize:18}}>Marketplace</Link>
      <div style={{flex:1}} />
      <Link to="/" style={{marginRight:12}}>Home</Link>
      <Link to="/cart" style={{marginRight:12}}>Cart</Link>
      {logged ? (
        <button onClick={logout} style={{padding:'6px 10px'}}>Logout</button>
      ) : (
        <Link to="/auth"><button style={{padding:'6px 10px'}}>Login</button></Link>
      )}
    </nav>
  );
}
