import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "../api/axios";

export default function VendorList() {
  const { category } = useParams(); // restaurants / grocery / fruits
  const [vendors, setVendors] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    async function loadVendors() {
      try {
        setLoading(true);

        // If category exists → send ?category=restaurants
        const url = category
          ? `/vendors?category=${category.toLowerCase()}`
          : "/vendors";

        console.log("Fetching:", url);

        const { data } = await axios.get(url);
        console.log("API Response:", data);

        setVendors(data);
      } catch (err) {
        console.error("Fetch error:", err);
      } finally {
        setLoading(false);
      }
    }

    loadVendors();
  }, [category]);

  if (loading) return <p>Loading vendors...</p>;

  return (
    <div>
      <h2 style={{ textTransform: "capitalize" }}>
        {category ? `${category} Vendors` : "All Vendors"}
      </h2>

      {vendors.length === 0 && <p>No vendors found.</p>}

      <div style={{ display: "grid", gap: "15px", marginTop: "20px" }}>
        {vendors.map((v) => (
          <Link
            to={`/vendor/${v._id}`}
            key={v._id}
            style={{
              border: "1px solid #ddd",
              padding: "15px",
              borderRadius: "10px",
              textDecoration: "none",
              color: "#000",
            }}
          >
            <h3>{v.name}</h3>
            <p>Type: {v.type}</p>
            <p>Address: {v.address}</p>
          </Link>
        ))}
      </div>
    </div>
  );
}
