import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom";

const VendorList = () => {
  const { category } = useParams();
  const [vendors, setVendors] = useState([]);

  useEffect(() => {
    if (!category) return;

    axios
      .get(`https://backendmodern.onrender.com/api/vendors/category/${category}`)
      .then((res) => {
        setVendors(res.data);
      })
      .catch((err) => {
        console.log("Vendor Fetch Error:", err);
      });
  }, [category]);

  return (
    <div>
      <h2>{category.toUpperCase()}</h2>

      {vendors.length === 0 ? (
        <p>No vendors found in this category.</p>
      ) : (
        vendors.map((v) => (
          <div key={v._id} className="vendor-card">
            <h3>{v.name}</h3>
            <p>{v.address}</p>
            <p>Category: {v.category}</p>
          </div>
        ))
      )}
    </div>
  );
};

export default VendorList;
