import { Link } from "react-router-dom";

export default function ProductCard({ p }) {
  return (
    <div style={{
      border:'1px solid #eee', borderRadius:8, padding:12, width:220, background:'#fff'
    }}>
      <Link to={`/product/${p._id}`} style={{textDecoration:'none',color:'inherit'}}>
        <div style={{height:140, display:'flex', alignItems:'center', justifyContent:'center', overflow:'hidden'}}>
          <img src={p.image || p.images?.[0] || "/placeholder.png"} alt={p.name||p.title} style={{maxHeight:'100%', maxWidth:'100%'}}/>
        </div>
        <h4 style={{margin:'8px 0 4px'}}>{p.name || p.title}</h4>
        <div style={{color:'#555'}}>â‚¹{p.price}</div>
      </Link>
    </div>
  );
}
